import { Component, OnInit, OnDestroy } from '@angular/core';
import { Subscription } from 'rxjs';
import { BagService } from '../path/to/bag.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit, OnDestroy {
  bagCount: number;
  private bagCountSubscription: Subscription;

  constructor(private bagService: BagService) {}

  ngOnInit() {
    this.bagCount = this.bagService.getBagCount();
    this.bagCountSubscription = this.bagService.bagCountChanged.subscribe(
      (count: number) => {
        this.bagCount = count;
        this.someFunction(); // Call your function here
      }
    );
  }

  ngOnDestroy() {
    if (this.bagCountSubscription) {
      this.bagCountSubscription.unsubscribe();
    }
  }

  someFunction() {
    // The function you want to call when bagCount changes
  }
}
