import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Product } from './models/product.model';

@Injectable({
  providedIn: 'root'
})
export class CartService {
private itemsInCart: Product[] = [];
  private total: number = 0;

  constructor() { }

  addToCart(product: Product) {
    this.itemsInCart.push(product);
    this.total += product.price; // Add product price to total
  }

  getCartItems() {
    return this.itemsInCart.slice(); // Returns a copy of the cart items array
  }

  getCartCount() {
    return this.itemsInCart.length; // Returns the count of items in the cart
  }

  getTotalPrice() {
    return this.total; // Returns the total price
  }
}
