import { Component } from '@angular/core';
import { Product } from '../models/product.model';
import { CartService } from '../cart.service';
import { FormsModule } from '@angular/forms';


@Component({
  selector: 'app-men',
  templateUrl: './men.component.html',
  styleUrls: ['./men.component.css']
})
export class MenComponent {   
  selectedSize: {[key: number]: string} = {};
  topsMen : Product[] = [
    {id:1, price: 59, imageUrl:"https://img.ltwebstatic.com/images3_pi/2022/10/18/16660703076e1bf7842fff192ebcd8c82a9d06f21e_thumbnail_600x.webp",
    size: ['S', 'M', 'L', 'XL', 'XXL'], inStock: false},
    {id:2, price: 59, imageUrl:"https://img.ltwebstatic.com/images3_pi/2022/11/24/1669258675c9ccdea7330f9276e83ccfc45633bffe_thumbnail_600x.webp",
    size: ['S', 'M', 'L', 'XL', 'XXL'], inStock: true},
    {id:3, price: 29, imageUrl:"https://img.ltwebstatic.com/images3_pi/2021/12/08/1638933026c22cae14e72ed612850c8c3202f211a0_thumbnail_600x.webp",
    size: ['S', 'M', 'L', 'XL', 'XXL'], inStock: true},

  ]

  constructor (private cartservices: CartService){

  }

  addToCart(product:Product){
    this.cartservices.addToCart(product);
  }
}
<div class="container">
    <div *ngFor="let product of topsMen" class="product-card">
        <img class="product-image" [src]="product.imageUrl"/>
        <div class="product-details">
            <p class="price">Price: {{ product.price }}<span>₪</span></p>
            <!--פה זה הסיליקטור של המידות -->
            <div class="sizes">
                <!--פה העדפתי לעשות דרופדאון -->
                <p *ngFor="let size in product.size" class="sss">{{size}}</p>
                    <div class="size-options">
                        <label for="size-select"> Size: </label>
                        <select id="size-select" class="size-dropdown">
                        <option *ngFor="let size of product.size" [value]="size">{{ size }}</option>
                        </select>
                    </div>
            </div>
            <!--פה מסתיים הסיקשן של המידות-->

            <!--הוספה לעגלה / אם אין כמותת אז הלחצן לא יופעל-->
            <!--את זה עשיתי בעזרת DISABLED CHECK-->
            <button (click)="addToCart(product)" class="add-to-bag" [disabled]="!product.inStock">ADD TO BAG</button>
            <p *ngIf="!product.inStock" class="out-of-stock">Out of Stock</p>
        </div>
    </div>
</div>
