import { Component, OnInit } from '@angular/core';
import { BagService } from '../bag.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  bagCount!: number;

  constructor(private bagService: BagService) {}

  ngOnInit() {
    this.bagCount = this.bagService.getBagCount();
  }

  updateCounter(count:number) {
    this.bagCount = this.count;
  }
}

    <header class="header">
        <nav>
            <ul>
                <li routerLink="/home" routerLinkActive="active-link">Home</li>
                <li routerLink="/women" routerLinkActive="active-link">Women</li>
                <li routerLink="/men" routerLinkActive="active-link">Men</li>
                <li class="cart-li">
                    <button class="cart-button" routerLink="/bag" aria-label="View cart">
                        <img src="assets/cart-black.png" alt="Cart" class="cart-svg"/>
                        <span class="cart-count">{{bagCount}}</span>
                    </button>
                </li>
            </ul>
        </nav>
    </header>
